	RGG_UNDC = "Nemrtví ghúlové",
	RGG_UNDG = "Nemrtví větší",
	RGG_UNDS = "Nemrtví kostliví",
	RGG_UNDP = "Nemrtví nehmotní",
	RGG_BEAR = "Zvíře Medvěd",
